mapper.select:
SELECT 

FROM oa_notify_record a

WHERE a.id = #{id}

mapper.insert:
INSERT INTO oa_notify_record( id, oa_notify_id, user_id, read_flag, read_date )
( SELECT #{e.id}, #{e.oaNotify.id}, #{e.user.id}, #{e.readFlag}, #{e.readDate}
FROM dual
)

mapper.update:
UPDATE oa_notify_record SET 	
	read_flag = #{readFlag},
	read_date = #{readDate}
WHERE oa_notify_id = #{oaNotify.id}
	AND user_id = #{user.id}
	AND read_flag != '1'
