mapper.select:
SELECT 

FROM coupon a

WHERE a.id = #{id}

SELECT 

FROM coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.code = #{code}


		AND a.status = #{status}


		AND a.type = #{type}


		AND a.channel = #{channel}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE coupon SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
