mapper.select:
SELECT 

FROM issue a

WHERE a.id = #{id}

SELECT 

FROM issue a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.major_id = #{majorId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}




		ORDER BY name,num


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM issue a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE issue SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM issue a

WHERE major_id = #{majorId} and name = #{name} and num = #{num}

	and id != #{id}
