mapper.select:
SELECT 

FROM issue_teachers a

WHERE a.id = #{id}

SELECT 

FROM issue_teachers a



		a.issue_id = #{issueId}


		a.teacher_id = #{teacher.id}

mapper.insert:
INSERT INTO issue_teachers(
	id,
	issue_id,
	teacher_id
) VALUES (
	#{id},
	#{issueId},
	#{teacher.id}
)

mapper.update:
UPDATE issue_teachers SET 	
	issue_id = #{issueId},
	teacher_id = #{teacher.id}
WHERE id = #{id}

DELETE FROM issue_teachers


		and issue_id = #{issueId}


		and id = #{id}
