mapper.select:
SELECT 

FROM live_course a

WHERE a.id = #{id}

SELECT
FROM live_course a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.lci_kind = #{lciKind}
AND a.major_id = #{majorId}
AND a.company LIKE '%'||#{company}||'%' '%'+#{company}+'%' concat('%',#{company},'%')
AND a.lecturer LIKE '%'||#{lecturer}||'%' '%'+#{lecturer}+'%' concat('%',#{lecturer},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM live_course a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE live_course SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
