mapper.select:
SELECT 

FROM teachers a

WHERE a.id = #{id}

SELECT
FROM teachers a
a.del_flag = #{DEL_FLAG_NORMAL}
AND b.mobile LIKE '%'||#{user.mobile}||'%' '%'+#{user.mobile}+'%' concat('%',#{user.mobile},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM teachers a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE teachers SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
