mapper.select:
SELECT 

FROM user_info a

WHERE a.id = #{id}

SELECT
FROM user_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.identity_number LIKE '%'||#{identityNumber}||'%' '%'+#{identityNumber}+'%' concat('%',#{identityNumber},'%')
AND a.tel LIKE '%'||#{tel}||'%' '%'+#{tel}+'%' concat('%',#{tel},'%')
AND a.email LIKE '%'||#{email}||'%' '%'+#{email}+'%' concat('%',#{email},'%')
AND a.area = #{area}
AND a.currentstatus = #{currentstatus}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM user_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE user_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
