mapper.select:
SELECT 

FROM vedio a


a.del_flag = #{DEL_FLAG_NORMAL}

AND a.id = #{id}

AND a.url = #{url}

SELECT
FROM vedio a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.id = #{id}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.url LIKE '%'||#{url}||'%' '%'+#{url}+'%' concat('%',#{url},'%')
AND a.status = #{status}
AND (b.id= #{resourseType.id} OR b.pids like CONCAT('%,', #{resourseType.id}, ',%') )
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM vedio a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE vedio SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT 

FROM vedio a 

WHERE a.name = #{name} AND a.del_flag = #{DEL_FLAG_NORMAL}

	and a.id != #{id}

SELECT 

FROM vedio a 

WHERE a.url = #{url} AND a.del_flag = #{DEL_FLAG_NORMAL}

	and a.id != #{id}
