SELECT 

FROM wclass_students a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.class_id = #{wclass.id}


		AND c.issue_id = #{wclass.issueId}


		AND b.name like concat('%',#{student.name},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wclass_students a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
